mapper.select:
select from t_enterprise_report report left join t_enterprise ent on report.enterprise_id = ent.enterprise_id where 1 = 1
AND report.enterprise_report_id like concat('%', #{reportId}, '%')
AND report.report_name like concat('%', #{reportName}, '%')
AND report.enterprise_id like concat('%', #{enterpriseId}, '%')
AND report.report_template_id like concat('%', #{reportTemplateId}, '%')
AND report.state = #{state}
AND report.publish_state = #{publishState}
AND date_format(make_date,'%Y-%m-%d') >= date_format(#{params.beginTime},'%Y-%m-%d')
AND date_format(make_date,'%Y-%m-%d') <= date_format(#{params.endTime},'%Y-%m-%d')
AND report.make_cycle = #{makeCycle}
AND ent.name = #{enterpriseName}

mapper.update:
update t_enterprise_report
publisher = #{publisher}, publish_state = '01'
where enterprise_report_id = #{enterpriseReportId}

update.foreach:
update t_enterprise_target_data
publisher = #{item.publisher, jdbcType=INTEGER}, publish_date = sysdate(), publish_state = '01'
where enterprise_target_data_id = #{item.enterpriseTargetDataId, jdbcType=INTEGER}

mapper.select:
select enterprise_report_Id, report_template_id, enterprise_id, make_cycle, make_date, state, publish_state, report_name
from t_enterprise_report
where enterprise_id = #{enterpriseId}
